package main

import (
	"bytes"
	"encoding/gob"
	"fmt"
	"log"
	"os"
	"path"
)

// Proverb states a general truth or piece of advice.
type Proverb struct {
	ID       int
	Value    string
	reviewed bool
}

// UnmarshalBinary decodes the form generated by MarshalBinary.
func (p *Proverb) UnmarshalBinary(data []byte) error {
	b := bytes.NewBuffer(data)
	_, err := fmt.Fscanf(b, "ID=%d Value=%q reviewed=%t\n", &p.ID, &p.Value, &p.reviewed)
	return err
}

func main() {
	filename := path.Join("..", "proverbs.gob")
	file, err := os.Open(filename)
	if err != nil {
		log.Fatalln(err)
	}
	defer file.Close()

	var proverbs []Proverb

	dec := gob.NewDecoder(file)
	if err := dec.Decode(&proverbs); err != nil {
		log.Fatalln(err)
	}

	for _, p := range proverbs {
		log.Printf("%#v", p)
	}
}
